import React, { Component } from "react";
import './hangman.css'
import { randomWord } from "./words.js";


import img0 from "./images/0.jpg"
import img1 from "./images/1.jpg"
import img2 from "./images/2.PNG"
import img3 from "./images/3.PNG"
import img4 from "./images/4.PNG"
import img5 from "./images/5.PNG"

class Hangman extends Component {
    static defaultProps = {
        maxWrong: 6,
        images: [img0, img1, img2, img3, img4, img5],
    }

    constructor(props) {
        super(props);
        this.state = {
            mistake: 0,
            guessed: new Set([]),
            answer: randomWord(),
        }
    }

    guessedWord() {
        return this.state.answer.split("").map(letter => (this.state.guessed.has(letter) ? letter : " _ "))
    }

    generateButtons() {
        return "abcdefghijklmnopqrstuvwxyz"
    }


    render() {
        const gameOver = this.state.mistake >= this.props.maxWrong;
        let gameState = this.generateButtons();

        return (
            <div className="Hangman container">
                <h1 className="hangmanText">Hangman</h1>
                {/* кол-во не правельных ответов в  Hangman */}
                <div>Wrong Answers: {this.state.mistake} of {this.props.maxWrong}</div>
                {/* изображения Hangman */}
                <div>
                    <img className="hangmanImg" src={this.props.images[this.state.mistake]} alt="image" />
                </div>
                {/* Guess the word*/}
                <div>
                    <p>Guess the word:</p>
                    <p>
                        {!gameOver ? this.guessedWord() : this.state.answer}
                    </p>
                    <p>{gameState}</p>
                </div>


            </div>
        )
    }
}

// Hangman.defaultProps = {
//     maxWrong: 6,
//     images: [img0, img1, img2, img3, img4, img5]
// }

export default Hangman;
