class Field extends Component {
  render() {
    const holder = 'Enter here';
    const styledFiled = {
      width: '300px',
    }
    return <input
      placeholder={holder}
      type="text"
      style={styledFiled} />

  }
}

function Btn() {
  const text = "Log in"
  const logged = true;
  return <button>{logged ? 'Enter' : text}</button>
}

function App() {
  return (
    <div className="App">
      <Header />
      <Field />
      <Btn />
    </div>
  );
}

export { Header };
export default App;
