import React from "react";
import PropTypes from "prop-types"


const styles = {
    li: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: ".5rem 1rem",
        border: ".1px solid black",
        borderRadius: "4px",
        marginBottom: ".5rem "
    },
    input: {
        marginRight: '1rem'
    }
}


function TodoItem({ todo, index, onBoxClick }) {
    // console.log("todo", todo)
    const classes = [];

    if (todo.completed) {
        classes.push('done')
    }
    return (
        <li style={styles.li}>
            <span className="{classes.join(' ')}">
                <input type="checkbox" style={styles.input} onChange={() => onBoxClick(todo.id)} />
                <strong>{index + 1}</strong>
                &nbsp;
                {todo.title}
            </span>
            <button className="rm">&times;</button>

        </li>
    )
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired,
    index: PropTypes.number,
    onBoxClick: PropTypes.func.isRequired
}

export default TodoItem
